import React from 'react'
import './styles/BadgeDetails.css'
import confLogo from '../images/platziconf-logo.svg'
import api from '../api';
import PageLoader from '../componentes/PageLoading'
import PageError from '../componentes/PageError'
import Badge from '../componentes/Badge'
import {Link} from 'react-router-dom'


class BadgeDetails extends React.Component{
    state = {
        loading:true,
        error: null,
        data: undefined
    }

    componentDidMount(){
        this.fetchData()
    }

    fetchData= async () => {
        this.setState({loading: true, error:null})
        try {
            const data = await api.badges.read(this.props.match.params.badgeId)
            this.setState({loading: false,data: data})
        }
        catch(error){
            this.setState({loading: false, error: error})
        }
    }

    render(){
        if(this.state.loading) {
            return <PageLoader />
        }

        if(this.state.error) {
            return <PageError error={this.state.error}/>
        }

        const badge = this.state.data
        console.log(badge)

        return(
            <div>
                <div className="BadgeDetails__hero">
                    <div className="contaire">
                        <div className="row">
                            <div className="col-6">
                                <img src={confLogo} alt="logo de conferencia"/>
                            </div>
                            <div className="col-6 BadgeDetails__hero-attendant-name" >
                                <h1>{badge.firstName} {badge.lastName}</h1>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <Badge firstName={badge.firstName} lastName={badge.lastName} email={badge.email} jobTitle={badge.jobTitle} twitter={badge.twitter} />
                        </div>
                        <div className="col">
                            <h2>Actions</h2>
                            <div>
                                <Link className="btn btn-primary mb-4" to={`/badges/${badge.id}/edit`}>Edit</Link>
                            </div>
                            <div>
                                <button className="btn btn-danger">Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default BadgeDetails
